mapper.select:
SELECT A.i_trans, A.chk, B.i_user ,B.nick, B.profile_img, B.addr, B.grade, A.saleI_user FROM t_trans A LEFT JOIN t_user B ON A.i_user = B.i_user
LEFT JOIN t_board C ON A.i_board = C.i_board
WHERE A.i_board = #{i_board}

SELECT chk FROM t_trans
WHERE i_board = #{i_board}
AND i_user = #{i_user}

SELECT i_trans FROM t_trans
WHERE i_board = #{i_board}
AND i_user = #{i_user}

mapper.update:
UPDATE t_board
SET sold = 1
WHERE i_board = #{i_board}

mapper.delete:
DELETE FROM t_trans
WHERE i_board = #{i_board}
AND i_user = #{i_user}
